mapper.select:
SELECT h.HNO AS hno , h.MNO AS mno , TO_CHAR(h.HDATE,'YYYY.MM.DD') AS hdateStr , NVL(TO_CHAR(h.SLEEPTIME,'HH24:MI'), '00:00') AS sleeptimeStr , h.HWEIGHT AS hweight , h.HWEIGHT AS weight , h.WATERAMOUNT AS wateramount , h.EXERCISE AS exercise , h.FOOD AS food
, TO_CHAR(h.HDATE,'YYYY-MM-DD') AS hdate
FROM Health_Daily_Log h JOIN member m ON m.mno = h.mno WHERE h.MNO = #{mno}
AND TRUNC(h.HDATE) = TO_DATE(#{date}, 'YYYY-MM-DD')
AND h.HNO < #{cursor}
ORDER BY h.HDATE DESC, h.HNO DESC FETCH FIRST #{limit} ROWS ONLY

mapper.update:
UPDATE Health_Daily_Log SET HDATE = CASE WHEN #{hdate} IS NOT NULL AND #{hdate} != '' THEN TO_DATE(#{hdate}, 'YYYY-MM-DD') ELSE HDATE END,
SLEEPTIME = CASE WHEN #{sleeptime} IS NOT NULL AND #{sleeptime} != '' THEN TO_DATE( (CASE WHEN #{hdate} IS NOT NULL AND #{hdate} != '' THEN #{hdate} ELSE TO_CHAR(SLEEPTIME,'YYYY-MM-DD') END) || ' ' || LPAD(SUBSTR(#{sleeptime},1,INSTR(#{sleeptime},':')-1), 2, '0') || ':' || LPAD(SUBSTR(#{sleeptime},INSTR(#{sleeptime},':')+1), 2, '0'), 'YYYY-MM-DD HH24:MI' ) ELSE SLEEPTIME END,
HWEIGHT = NVL(#{hweight}, HWEIGHT), WATERAMOUNT = NVL(#{wateramount}, WATERAMOUNT), EXERCISE = NVL(#{exercise}, EXERCISE), FOOD = NVL(#{food}, FOOD) WHERE HNO = #{hno} AND MNO = #{mno}
